mapper.select:
SELECT COUNT(*)
FROM COMMENT
WHERE BOARD_NO = #{boardNo}

SELECT COUNT(*) FROM COMMENT JOIN MEMBER USING(MEMBER_NO) WHERE BOARD_NO = #{boardNo}
AND MEMBER_NAME LIKE '%' || #{query} || '%'
AND COMMENT_CONTENT LIKE '%' || #{query} || '%'
